package linter;

import java.io.IOException;
import java.nio.file.Path;
import java.nio.file.Paths;
import java.util.Scanner;

public class App {
    public static void main(String[] args) throws IOException {
        //Path to file to read
        Path gatesFile = Paths.get("app/src/main/resources/gates.js");
        linter(gatesFile);
    }
    public static int linter (Path gatesFile) throws IOException {

        Scanner scanner = new Scanner(gatesFile);
        int counter = 0;
        int lineCount = 0;
        while(scanner.hasNextLine()){
            lineCount++;
            String line = scanner.nextLine();
            //Conditions for linter
            if(!line.endsWith(";")){
                if (line.endsWith("{") || line.endsWith("}") || line.endsWith("else") || line == "" || line.contains("if")){
                }
                else{
                    counter++;
                    System.out.println("Line: " + lineCount + " Missing semicolon");
                }
            }
        }
        return counter;
    }
}
